import React, { useState } from "react";
import "./styles.scss";

export const Coffeebay = () => {
  //let orders = ["order 1", "order 2", "order 3"];
  const [orders, setOrders] = useState(["order 1", "order 2", "order 3"]);
  let listItems = orders.map((orders) => {
    return <li>{orders}</li>;
  });

  const addOrder = (text) => {
    const newOrder = [...orders, { text }];
    setOrders(newOrder);
  };

  const submit = (e) => {
    e.preventDefault();
    if (!orders) return;
    addOrder(orders);
    setOrders("");
  };

  return (
    <div className="body">
      <div className="orderlist">
        <ul>{listItems}</ul>
        <form className="form" onSubmit={addOrder}>
          <input
            name="order"
            type="text"
            className="input"
            placeholder="Order name"
            value={orders} 
            onChange={e => setOrders(e.target.value)}
          ></input>
          <div>
            <button className="button" type="submit">
              ADD ORDER
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};
